    limits: { fieldNameSize: 2 }
  },
  { source: ['foo=bar&baz=bla'],
    expected: [
      ['foo',
       'ba',
       { nameTruncated: false,
         valueTruncated: true,
         encoding: 'utf-8',
         mimeType: 'text/plain' },
      ],
      ['baz',
       'bl',
       { nameTruncated: false,
         valueTruncated: true,
         encoding: 'utf-8',
         mimeType: 'text/plain' },
      ],
    ],
    what: 'Limits: truncated field value',
    limits: { fieldSize: 2 }
  },
  { source: ['foo=bar&baz=bla'],
    expected: [
      ['fo',
       'ba',
       { nameTruncated: true,
         valueTruncated: true,
         encoding: 'utf-8',
         mimeType: 'text/plain' },
      ],
      ['ba',
       'bl',
       { nameTruncated: true,
         valueTruncated: true,
         encoding: 'utf-8',
         mimeType: 'text/plain' },
      ],
    ],
    what: 'Limits: truncated field name and value',
    limits: { fieldNameSize: 2, fieldSize: 2 }
  },
  { source: ['foo=bar&baz=bla'],
    expected: [
      ['fo',
       '',
       { nameTruncated: true,
         valueTruncated: true,
         encoding: 'utf-8',
         mimeType: 'text/plain' },
      ],
      ['ba',
       '',
       { nameTruncated: true,
         valueTruncated: true,
