  },
  { source: ['&'],
    expected: [],
    what: 'Ampersand'
  },
  { source: ['&&&&&'],
    expected: [],
    what: 'Many ampersands'
  },
  { source: ['='],
    expected: [
      ['',
       '',
       { nameTruncated: false,
         valueTruncated: false,
         encoding: 'utf-8',
         mimeType: 'text/plain' },
      ],
    ],
    what: 'Assigned value, empty name and value'
  },
  { source: [''],
    expected: [],
    what: 'Nothing'
  },
];

for (const test of tests) {
  active.set(test, 1);

  const { what } = test;
  const charset = test.charset || 'utf-8';
  const bb = busboy({
    limits: test.limits,
    headers: {
      'content-type': `application/x-www-form-urlencoded; charset=${charset}`,
    },
  });
  const results = [];

  bb.on('field', (key, val, info) => {
    results.push([key, val, info]);
  });

  bb.on('file', () => {
    throw new Error(`[${what}] Unexpected file`);
  });

  bb.on('close', () => {
    active.delete(test);

    assert.deepStrictEqual(
      results,
      test.expected,
      `[${what}] Results mismatch.\n`
        + `Parsed: ${inspect(results)}\n`
        + `Expected: ${inspect(test.expected)}`
    );
  });

  for (const src of test.source) {
    const buf = (typeof src === 'string' ? Buffer.from(src, 'utf8') : src);
    bb.write(buf);
  }
  bb.end();
}

// Byte-by-byte versions
for (let test of tests) {
  test = { ...test };
  test.what += ' (byte-by-byte)';
  active.set(test, 1);

  const { what } = test;
  const charset = test.charset || 'utf-8';
  const bb = busboy({
    limits: test.limits,
    headers: {
      'content-type': `application/x-www-form-urlencoded; charset="${charset}"`,
    },
  });
  const results = [];

  bb.on('field', (key, val, info) => {
    results.push([key, val, info]);
  });

  bb.on('file', () => {
    throw new Error(`[${what}] Unexpected file`);
  });

  bb.on('close', () => {
    active.delete(test);

    assert.deepStrictEqual(
      results,
      test.expected,
      `[${what}] Results mismatch.\n`
        + `Parsed: ${inspect(results)}\n`
        + `Expected: ${inspect(test.expected)}`
    );
  });

  for (const src of test.source) {
    const buf = (typeof src === 'string' ? Buffer.from(src, 'utf8') : src);
    for (let i = 0; i < buf.length; ++i)
      bb.write(buf.slice(i, i + 1));
  }
  bb.end();
}

{
  let exception = false;
  process.once('uncaughtException', (ex) => {
    exception = true;
    throw ex;
  });
  process.on('exit', () => {
    if (exception || active.size === 0)
      return;
    process.exitCode = 1;
    console.error('==========================');
    console.error(`${active.size} test(s) did not finish:`);
    console.error('==========================');
    console.error(Array.from(active.keys()).map((v) => v.what).join('\n'));
  });
}

'use strict';

const { spawnSync } = require('child_process');
const { readdirSync } = require('fs');
const { join } = require('path');

const files = readdirSync(__dirname).sort();
for (const filename of files) {
  if (filename.startsWith('test-')) {
    const path = join(__dirname, filename);
    console.log(`> Running ${filename} ...`);
    const result = spawnSync(`${process.argv0} ${path}`, {
      shell: true,
      stdio: 'inherit',
      windowsHide: true
    });
    if (result.status !== 0)
      process.exitCode = 1;
  }
}
